jQuery(window).load(function($) {
 
});

jQuery(document).ready(function($) {
	/* ---------------------------------
		MENU Dropdowns
	----------------------------------- */
	$('ul.nav').each(function(){
		// find menu items with children.
		$(this).find('li').has('ul').addClass('dropdown')
		.append('');
	});
	
	$('ul.nav li').hover(function(){
		$(this).find('ul:first').stop(true, true).fadeIn('fast');
		$(this).addClass('hover');
	},
	function(){
		$(this).find('ul').stop(true, true).fadeOut('slow');
		$(this).removeClass('hover');
	});

	/* ---------------------------------
		FLEXSLIDER
	----------------------------------- */
	$('#top_slider').flexslider({
	slideshowSpeed: 20000,           //Integer: Set the speed of the slideshow cycling, in milliseconds
	directionNav: false,             //Boolean: Create navigation for previous/next navigation? (true/false)
	controlsContainer: "#top_slider"
	});
	$('#tubepressflex').flexslider({
	slideshowSpeed: 20000,           //Integer: Set the speed of the slideshow cycling, in milliseconds
	directionNav: true,            //Boolean: Create navigation for previous/next navigation? (true/false)
	controlsContainer: "#tubepressflex"
	});
	/* ---------------------------------
			ISOTOPE
	----------------------------------- */
	$('#articles').isotope({
	// options
	itemSelector : 'article',
	layoutMode : 'fitRows'
	}); 
	/*---------------------------------
		MANUAL CALLS TO TWITTER BOOTSTRAP STUFF
	-----------------------------------*/
	 $('#myID').popover({ 
    html : true,
          placement : 'bottom', //placement of the popover. also can use top, bottom, left or right

	title: function() {
      return $("#myformIdorClassTitle").html();
    },
    content: function() {
      return $("#myFormIdorClassForm").html();
    }
});
	 
$("[rel=drevil]").popover({
      placement : 'top', //placement of the popover. also can use top, bottom, left or right
      title : '<div style="text-align:center; color:red; text-decoration:underline; font-size:14px;"> Muah ha ha</div>', //this is the top title bar of the popover. add some basic css
      html: 'true', //needed to show html of course
      content : '<div id="popOverBox"></div>' //this is the content of the html box. add the image here or anything you want really.
	});

	$("#example").popover();
	$('.tiptip').tooltip(); 
	$('.carousel').carousel({
		interval: false
	   })
	/*---------------------------------
		FIT VID
	-----------------------------------*/
	// Basic FitVids Test
	$(".container").fitVids();
	/*---------------------------------
		FANCYBOX
	-----------------------------------*/
	$(".fancysearch2")
    .fancybox({
        padding:0,
		closeBtn:false,	
    });
	$(".fancysearch").fancybox({
	padding : 0,
	closeBtn:false,	
	beforeShow: function(){
		$(".fancybox-skin").css({
		backgroundColor: 'transparent',
		'box-shadow': 'none'});
        }
	});
	$(".fancycontact").fancybox({
	padding : 0,
	closeBtn:false,	
	beforeShow: function(){
		$(".fancybox-skin").css({
		backgroundColor: 'transparent',
		'box-shadow': 'none'});
        }
	});	
	$(".fancyvid").fancybox();
	$(".fancycontact1").fancybox();
	$(".fancyperson")
		.attr('rel', 'gallery')
		.fancybox({
			openEffect  : 'none',
			closeEffect : 'none',
			nextEffect  : 'none',
			prevEffect  : 'none',
			padding     : 0,
			closeBtn	: false,
			arrows : false,
		});	
	$(".photolink").fancybox();
		
});

